import React, { Component } from 'react'



const scaleNames = {
  c: 'Celsius',
  f: 'Fahrenheit'
};
export default class Far extends Component {
    state = {
        temperature:"enter tem in Fahrenheit",
    }
    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });


    };
    render() {
        return (
            <>
                <fieldset>
                    <legend>Enter temperature in Fahrenheit:</legend>
                    <input name="temperature" value={this.state.temperature}
                        onChange={this.handleChange} />                    
                </fieldset>
            </>
        )
    }
}
